<title>Edit Data member</title><fieldset><legend>Edit Data member</legend>    <?php    $id = @$_GET['id'];    $sql = mysql_query("select * from tb_member where id_member = '$id'") or die (mysql_error());    $data = mysql_fetch_array($sql);    ?>    <table class='table table-condensed table-bordered'>    <tr>    	<td width='150px' scope='row'>Nama Lengkap</td>        <td>:</td>        <td><input class='form-control'  type="text" id="nm" value="<?php echo $data['nm']; ?>" /></td>    </tr>    <tr>    	<td>Jenis Kelamin</td>        <td>:</td>        <td>        	<select id="jk" class='form-control'>        		<?php                if($data['jk'] == "Laki-laki") { ?>                    <option class='form-control' value="Laki-laki" selected="selected">Laki-laki</option>                    <option class='form-control' value="Perempuan">Perempuan</option> <?php                } else if($data['jk'] == "Perempuan") { ?>                    <option class='form-control' value="Laki-laki">Laki-laki</option>                    <option class='form-control' value="Perempuan" selected="selected">Perempuan</option> <?php                }else{				?>                    <option class='form-control' value="Laki-laki">Laki-laki</option>                    <option class='form-control' value="Perempuan">Perempuan</option> <?php				}                ?>        	</select>        </td>    </tr>    <tr>    	<td valign="top">Alamat</td>        <td valign="top">:</td>        <td><textarea id="alamat" class='form-control'><?php echo $data['almt']; ?></textarea></td>    </tr>    <tr>    	<td>No. Telepon</td>        <td>:</td>        <td><input class='form-control'  type="text" id="tlp" value="<?php echo $data['notelp']; ?>" /></td>    </tr>	<tr>    	<td valign="top">Tanggal Daftar</td>        <td valign="top">:</td>        <td><input type="date" name="tgl1" id="tgl1" value="<?php echo $data['tgl_daftar']; ?>" class="form-control" placeholder="yyyy-mm-dd" /></td>    </tr>    <tr>    	<td valign="top">Tanggal Expired</td>        <td valign="top">:</td>        <td><input type="date" name="tgl2" id="tgl2" class="form-control" value="<?php echo $data['tgl_diperbarui']; ?>" placeholder="yyyy-mm-dd" /></td>    </tr>        <tr>    	<td></td>        <td></td>        <td><button id="edit" class="btn btn-primary">Edit</button></td>    </tr>    <tr>	    <td></td>    </tr>    </table><script>	$("#edit").click(function() {		var nm = $("#nm").val();		var member = $("#member").val();		var pass = $("#pass").val();		var jk = $("#jk").val();		var alamat = $("#alamat").val();		var tlp = $("#tlp").val();		var tgl1 = $("#tgl1").val();		var tgl2 = $("#tgl2").val();		if(nm == '') {			alert("Nama Lengkap tidak boleh kosong");			$("#nm").focus();		} else if(jk == '') {			alert("Jenis kelamin harus dipilih");			$("#jk").focus();		} else if(alamat == '') {			alert("Alamat tidak boleh kosong");			$("#alamat").focus();		} else if(tlp == '') {			alert("Nomor telepon belum diisi");			$("#tlp").focus();		} else {			$.ajax({				type : 'post',				url : 'inc/member/proses_edit_member.php',				data : 'nm='+nm+'&jk='+jk+'&alamat='+alamat+'&tlp='+tlp+'&tgl1='+tgl1+'&tgl2='+tgl2+'&id=<?php echo $id; ?>',				success : function(msg){					$("#hasil_edit").html(msg);				}			});		}	});	</script>    <div id="hasil_edit"></div></fieldset>