<title>Tambah member</title><fieldset><legend>Tambah member</legend>    <?php    $id = @$_GET['id'];    $sql = mysql_query("select * from tb_member where id_member = '$id'") or die (mysql_error());    $data = mysql_fetch_array($sql);$sekarang = date('ymd');	$thn_skr=date('Y');$bln_skr=date('m');$kode_user1=0;$carikode = mysql_query("select * from tb_member order by tgl_daftar desc") or die (mysql_error());$datakode = mysql_fetch_row($carikode);if($datakode) {	$tambah=6+strlen($kode_user1);	$nilaikode = substr($datakode[0], $tambah);	$kode = (int) $nilaikode;	$kode = $kode+1;	$hasilkode = $sekarang.$kode_user1.str_pad($kode, 3 , "0", STR_PAD_LEFT);} else {	$hasilkode = $sekarang.$kode_user1."001";}    ?>    <table class='table table-condensed table-bordered'>    <tr>    	<td width='150px' scope='row'>Kode Member</td>        <td>:</td>        <td><input class="form-control" type="text" id="kode" value="<?php echo $hasilkode; ?>" readonly /></td>    </tr>	<tr>    	<td width='150px' scope='row'>Nama Lengkap</td>        <td>:</td>        <td><input class="form-control" type="text" id="nm" value="<?php echo $data['nama_lengkap']; ?>" /></td>    </tr>	<tr>    	<td>Jenis Kelamin</td>        <td>:</td>        <td>        	<select id="jk" class="form-control">        							<option class="form-control" value="Laki-laki" >Laki-laki</option>                    <option class="form-control" value="Perempuan">Perempuan</option>         	</select>        </td>    </tr>    <tr>    	<td valign="top">Alamat</td>        <td valign="top">:</td>        <td><textarea id="alamat" class="form-control"><?php echo $data['alamat']; ?></textarea></td>    </tr>    <tr>    	<td>No. Telepon</td>        <td>:</td>        <td><input class="form-control" type="text" id="tlp" value="<?php echo $data['no_telepon']; ?>" /></td>    </tr>    <tr>    	<td valign="top">Tanggal Daftar</td>        <td valign="top">:</td>        <td><input type="date" value="<?php echo date('Y-m-d'); ?>" name="tgl1" id="tgl1" class="form-control" placeholder="yyyy-mm-dd" /></td>    </tr>    <tr>        <td valign="top">Email</td>        <td valign="top">:</td>        <td><input type="email" name="email" id="email" class="form-control" placeholder="alamat email" /></td>    </tr>    <tr>        <td valign="top">Tanggal Lahir</td>        <td valign="top">:</td>        <td><input type="date" name="tgl_lahir" id="tgl_lahir" class="form-control" placeholder="alamat tanggal lahir" /></td>    </tr>    <tr>        <td valign="top">Kota Asal</td>        <td valign="top">:</td>        <td><input type="text" name="kota" id="kota" class="form-control" placeholder="asal kota" /></td>    </tr>    <!--<tr>-->    <!--	<td valign="top">Tanggal Expired</td>-->    <!--    <td valign="top">:</td>-->    <!--    <td><input type="date" name="tgl2" id="tgl2" class="form-control"  placeholder="yyyy-mm-dd" /></td>-->    <!--</tr>-->    <tr>    	<td></td>        <td></td>        <td><button id="edit" class="btn btn-primary">Tambah</button></td>    </tr>    <tr>	    <td></td>    </tr>    </table><script>	$("#edit").click(function() {		var kode = $("#kode").val();		var nm = $("#nm").val();		var member = $("#member").val();		var pass = $("#pass").val();		var jk = $("#jk").val();		var alamat = $("#alamat").val();		var tlp = $("#tlp").val();		var tgl1 = $("#tgl1").val();        var email = $("#email").val();        var tgl_lahir = $("#tgl_lahir").val();        var kota = $("#kota").val();// 		var tgl2 = $("#tgl2").val();		if(nm == '') {			alert("Nama Lengkap tidak boleh kosong");			$("#nm").focus();		} else if(jk == '') {			alert("Jenis kelamin harus dipilih");			$("#jk").focus();		} else if(alamat == '') {			alert("Alamat tidak boleh kosong");			$("#alamat").focus();		} else if(tlp == '') {			alert("Nomor telepon belum diisi");			$("#tlp").focus();		} else {			$.ajax({				type : 'post',				url : 'inc/member/proses_tambah_member.php',				data : 'nm='+nm+'&jk='+jk+'&alamat='+alamat+'&tlp='+tlp+'&tgl1='+tgl1+'&kode='+kode+'&email='+email+'&tgl_lahir='+tgl_lahir+'&kota='+kota,				success : function(msg){					if(msg == 'admin') {                        alert("Data member berhasil di tambah");                        window.location.href='?page=member&action=view';                    } else {                        alert("Data member berhasil di tambah");                        window.location.href='?page=member&action=view';                    }				}			});		}	});	</script>    <div id="hasil_edit"></div></fieldset>